INSERT INTO `prodotto` (`IdProdotto`,`Nome`,`Prezzo`,`Quantita`,`Tipo`,`Categoria`,`Descrizione`) VALUES (5,'Pes 2016',27,10,'Videogiochi','PLAYSTATION','Pro Evolution Soccer 2016 scende in campo con un gameplay totalmente rinnovato e migliorato, parola Konami.  In questa edizione del gioco i movimenti dei giocatori sono stati resi più veloci e dinamici rispetto al passato.');
