INSERT INTO `prodotto` (`IdProdotto`,`Nome`,`Prezzo`,`Quantita`,`Tipo`,`Categoria`,`Descrizione`) VALUES (67,'Nintendo WII Mini',100,5,'Console','NINTENDO','La vera grande innovazione del Wii Mini è rappresentata dal Wii Remote Plus, ossia un controller senza fili che, grazie alla presenza di sensori di movimento e led infrarossi, permette di giocare interagendo con il proprio corpo.');
INSERT INTO `prodotto` (`IdProdotto`,`Nome`,`Prezzo`,`Quantita`,`Tipo`,`Categoria`,`Descrizione`) VALUES (68,'Nintendo DSi',80,5,'Console','NINTENDO','Il Nintendo DSi ha delle applicazioni accessibili da un menù. Anche il tasto power è stato spostato, ora è presente come i tasti start e select sotto la croce direzionale. Sia il touch screen che lo schermo superiore sono del 17% più grandi.');
INSERT INTO `prodotto` (`IdProdotto`,`Nome`,`Prezzo`,`Quantita`,`Tipo`,`Categoria`,`Descrizione`) VALUES (69,'Nintendo 2DS',88,3,'Console','NINTENDO','Nintendo elimina il display autostereoscopico dalla sua console portatile di punta e ne modifica l\'aspetto, optando per una forma piatta in luogo di quella richiudibile.');
INSERT INTO `prodotto` (`IdProdotto`,`Nome`,`Prezzo`,`Quantita`,`Tipo`,`Categoria`,`Descrizione`) VALUES (70,'Nintendo 3DS XL',190,8,'Console','NINTENDO','Nintendo 3DS XL è la nuova versione della console portatile di Nintendo, che rivoluziona il concetto di gioco grazie alla possibilità di poter visualizzare giochi ed immagini in 3D senza occhiali.');
INSERT INTO `prodotto` (`IdProdotto`,`Nome`,`Prezzo`,`Quantita`,`Tipo`,`Categoria`,`Descrizione`) VALUES (71,'Nintendo New 3DS',210,5,'Console','NINTENDO','Nintendo 3DS qui in una pregiata Limited Edition a tema Xenoblade Chronicles 3D, che ha rivoluzionato il concetto di gioco, grazie alla possibilità di poter visualizzare giochi ed immagini in 3D senza occhiali.');
INSERT INTO `prodotto` (`IdProdotto`,`Nome`,`Prezzo`,`Quantita`,`Tipo`,`Categoria`,`Descrizione`) VALUES (72,'Nintendo 3DS XL',180,6,'Console','NINTENDO','Nintendo 3ds xl incluso Mario Kart 7, la caratteristica principale di questa nuova versione è l\'incremento delle dimensioni degli schermi di circa il 90%, oltre ad un design integrale dal punto di vista estetico ed ergonomico.');
INSERT INTO `prodotto` (`IdProdotto`,`Nome`,`Prezzo`,`Quantita`,`Tipo`,`Categoria`,`Descrizione`) VALUES (73,'Nunchuck Nero',17,6,'Accessori','NINTENDO','Con il controller Nunchuk, inizia un viaggio nella più coinvolgente esperienza di gioco mai offerta. La combinazione degli innovativi controller Nunchuk e Remote Plus darà modo di sperimentare nuove prospettive di gioco.');
INSERT INTO `prodotto` (`IdProdotto`,`Nome`,`Prezzo`,`Quantita`,`Tipo`,`Categoria`,`Descrizione`) VALUES (74,'Nintendo Remote',36,9,'Accessori','NINTENDO','Il Nintendo Remote Plus, è il più avanzato sistema di controllo disponibile per le console Wii. Caratteristica principale è la sua capacità di rilevazione del movimento che consente di manipolare oggetti a schermo tramite il riconoscimento dei gesti.');
